script:
frappe.ui.form.on("Purchase Order", {
    refresh: function(frm) {
        if (frm.doc.docstatus === 1 &&
        frm.doc.supplier == "Proman Infrastructure Services Private Limited" &&
        !frm.doc.so_name) {
            frm.add_custom_button("Create SO in PISPL v15", function() {
                frappe.call({
                    freeze: 1,
                    freeze_message: "Creating Sales Order in PISPL v15 site.. Please wait till completion",

                    method: "site_integration.api.export_purchase_order_to_v15",
                    args: {
                        po_name: frm.doc.name
                    },

                    callback: function(response) {
                        if (response.message && response.message.status === "success") {
                            frappe.msgprint(response.message.message);
                            frm.reload_doc();

                        } else {
                            frappe.msgprint({
                                title: __("Failed"),
                                message: response.message ? response.message.message : "Failed to create Sales Order. Check Error Log.",
                                indicator: "red"
                            });
                        }
                    }
                });
            }).addClass("btn-primary");
        }
    }
});
